import React, { useState, useEffect } from 'react';
import { searchCourses } from '../API';
import Course from './Course'; // Importando o componente Course
import '../style/SearchCourse.css'; // Estilos para SearchCourse

const SearchCourse = () => {
  const [query, setQuery] = useState('');
  const [courses, setCourses] = useState([]);
  const [loading, setLoading] = useState(false);

  // Função para carregar cursos quando o componente for montado
  useEffect(() => {
    const loadCourses = async () => {
      setLoading(true);
      try {
        const results = await searchCourses('');  // Passa uma string vazia para pegar todos os cursos
        setCourses(results);
      } catch (error) {
        console.error('Erro ao carregar cursos', error);
      } finally {
        setLoading(false);
      }
    };

    loadCourses(); // Chama a função assim que o componente é montado
  }, []);  // O array vazio [] garante que a chamada seja feita apenas uma vez

  const handleSearch = async () => {
    setLoading(true); // Inicia o carregamento
    try {
      const results = await searchCourses(query);
      setCourses(results);
    } catch (error) {
      console.error('Erro ao buscar cursos', error);
    } finally {
      setLoading(false); // Finaliza o carregamento
    }
  };

  return (
    <div className="search-container">
      <h1>Pesquisar Cursos</h1>
      <div className="search-box">
        <input
          type="text"
          placeholder="Digite o nome do curso"
          value={query}
          onChange={e => setQuery(e.target.value)}
        />
        <button onClick={handleSearch} className="search-btn">
          <i className="fa fa-search"></i> {/* Ícone de lupa */}
        </button>
      </div>

      {loading && <p>Carregando...</p>} {/* Exibe mensagem de carregamento */}

      <div className="courses-list">
        {courses.map(course => (
          <Course key={course.id} course={course} /> // Usando o componente Course
        ))}
      </div>
    </div>
  );
};

export default SearchCourse;
