import React, { useState } from 'react';
import './App.css';  // Arquivo de estilos, se necess√°rio

// Exemplo de dados de cursos
const cursos = [
  {
    id: 1,
    titulo: "Curso de React",
    preco: "R$ 199,00",
    imagem: "https://via.placeholder.com/150"
  },
  {
    id: 2,
    titulo: "Curso de JavaScript",
    preco: "R$ 150,00",
    imagem: "https://via.placeholder.com/150"
  },
  {
    id: 3,
    titulo: "Curso de Python",
    preco: "R$ 120,00",
    imagem: "https://via.placeholder.com/150"
  }
];

function App() {
  const [filtro, setFiltro] = useState("");
  const [cursosFiltrados, setCursosFiltrados] = useState(cursos);

  // Fun√ß√£o de pesquisa
  const handlePesquisa = (e) => {
    const valor = e.target.value;
    setFiltro(valor);

    const cursosFiltrados = cursos.filter(curso => 
      curso.titulo.toLowerCase().includes(valor.toLowerCase())
    );
    setCursosFiltrados(cursosFiltrados);
  };

  return (
    <div className="App">
      <div className="pesquisa">
        <input
          type="text"
          value={filtro}
          onChange={handlePesquisa}
          placeholder="Pesquisar cursos..."
        />
        <button>
          üîç
        </button>
      </div>

      <div className="lista-cursos">
        {cursosFiltrados.length > 0 ? (
          cursosFiltrados.map(curso => (
            <div className="curso" key={curso.id}>
              <img src={curso.imagem} alt={curso.titulo} />
              <h3>{curso.titulo}</h3>
              <p>{curso.preco}</p>
            </div>
          ))
        ) : (
          <p>Nenhum curso encontrado</p>
        )}
      </div>
    </div>
  );
}

export default App;
