// src/components/SignUp.js
import React, { useState } from 'react';

function SignUp({ setUserRole, setUserData }) {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    password: '',
  });

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    
    // Aqui você pode fazer o envio para o backend ou apenas simular o cadastro.
    // Vamos simular que o usuário foi cadastrado com sucesso.
    
    // Por exemplo:
    setUserRole('user');  // Simula o login do usuário
    setUserData(formData);  // Simula o armazenamento dos dados do usuário.
    
    alert('Cadastro realizado com sucesso!');
  };

  return (
    <div>
      <h2>Cadastro</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="name">Nome:</label>
          <input
            type="text"
            id="name"
            name="name"
            value={formData.name}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label htmlFor="email">E-mail:</label>
          <input
            type="email"
            id="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label htmlFor="password">Senha:</label>
          <input
            type="password"
            id="password"
            name="password"
            value={formData.password}
            onChange={handleChange}
            required
          />
        </div>
        <button type="submit">Cadastrar</button>
      </form>
    </div>
  );
}

export default SignUp;
