import React, { useState } from "react";
import PurchaseModal from "./PurchaseModal";

const CourseCard = ({ curso }) => {
  const [showModal, setShowModal] = useState(false);

  return (
    <div className="curso" style={{ width: "300px", padding: "20px", border: "1px solid #ccc", borderRadius: "10px", textAlign: "center", boxShadow: "0 4px 8px rgba(0, 0, 0, 0.2)" }}>
      {/* Imagem do curso */}
      <img src={curso.imagem} alt={curso.titulo} style={{ width: "100%", height: "200px", objectFit: "cover", borderRadius: "10px" }} />
      
      {/* Título e preço do curso */}
      <h3>{curso.titulo}</h3>
      <p>{curso.preco}</p>

      {/* Botão de compra */}
      <button onClick={() => setShowModal(true)} style={{ backgroundColor: "#28a745", color: "white", padding: "10px", border: "none", borderRadius: "5px", cursor: "pointer" }}>
        Comprar
      </button>

      {/* Modal de compra */}
      {showModal && <PurchaseModal curso={curso} onClose={() => setShowModal(false)} />}
    </div>
  );
};

export default CourseCard;
