import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom'; // Importando o hook de navegação
import { loginUser } from '../API';

const Login = ({ setUserRole, setUserData }) => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate(); // Para redirecionar após login

  const handleLogin = async () => {
    try {
      const data = await loginUser(email, password);
      setUserRole(data.role);
      setUserData(data);

      if (data.role === 'admin') {
        navigate('/add-course'); // Redireciona para a página de adicionar curso
      } else {
        navigate('/search-course'); // Redireciona para a página de pesquisa de curso
      }
    } catch (error) {
      console.error('Falha no login', error);
      alert('Login falhou! Verifique suas credenciais.');
    }
  };

  return (
    <div>
      <h1>Login</h1>
      <input type="email" placeholder="Email" value={email} onChange={e => setEmail(e.target.value)} />
      <input type="password" placeholder="Senha" value={password} onChange={e => setPassword(e.target.value)} />
      <button onClick={handleLogin}>Entrar</button>
    </div>
  );
};

export default Login;
