import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import Login from './components/Login';
import ForgotPassword from './components/ForgotPassword';
import SearchCourse from './components/SearchCourse';
import AddCourse from './components/AddCourse';
import RemoveCourse from './components/RemoveCourse';
import ViewPurchaseDetails from './components/ViewPurchaseDetails';
import SignUp from './components/SignUp';

function App() {
    const [userRole, setUserRole] = useState(localStorage.getItem('userRole') || null);
    const [userData, setUserData] = useState(JSON.parse(localStorage.getItem('userData')) || null);

    useEffect(() => {
        if (userRole) {
            localStorage.setItem('userRole', userRole);
            localStorage.setItem('userData', JSON.stringify(userData));
        }
    }, [userRole, userData]);

    const handleLogout = () => {
        setUserRole(null);
        setUserData(null);
        localStorage.removeItem('userRole');
        localStorage.removeItem('userData');
    };

    return (
        <Router>
            <Routes>
                {/* Sempre começa pelo login */}
                <Route path="/" element={<Login setUserRole={setUserRole} setUserData={setUserData} />} />
                <Route path="/signup" element={<SignUp />} />
                <Route path="/forgot-password" element={<ForgotPassword />} />

                {/* Só exibe essas rotas se o usuário estiver logado */}
                {userRole ? (
                    <>
                        <Route path="/search-course" element={<SearchCourse />} />
                        <Route path="/view-purchase-details" element={<ViewPurchaseDetails />} />
                        {userRole === 'admin' && (
                            <>
                                <Route path="/add-course" element={<AddCourse userData={userData} />} />
                                <Route path="/remove-course" element={<RemoveCourse userData={userData} />} />
                            </>
                        )}
                        <Route path="*" element={<Navigate to="/search-course" />} /> {/* Redireciona para cursos */}
                    </>
                ) : (
                    <Route path="*" element={<Navigate to="/" />} /> // Sempre leva para login se não estiver autenticado
                )}
            </Routes>

            {/* Navbar só aparece depois do login */}
            {userRole && (
                <nav>
                    <ul>
                        <li>
                            <a href="/search-course">Buscar Cursos</a>
                        </li>
                        {userRole === 'admin' && (
                            <>
                                <li>
                                    <a href="/add-course">Adicionar Curso</a>
                                </li>
                                <li>
                                    <a href="/remove-course">Remover Curso</a>
                                </li>
                            </>
                        )}
                        <li>
                            <a href="/view-purchase-details">Minhas Compras</a>
                        </li>
                        <li>
                            <button onClick={handleLogout}>Logout</button>
                        </li>
                    </ul>
                </nav>
            )}
        </Router>
    );
}

export default App;
