// src/API.js
import axios from 'axios';

const apiUrl = 'http://localhost:3000';  // Atualize com o endpoint correto

// Função para fazer o login do usuário
export const loginUser = async (email, password) => {
  try {
    const response = await fetch('http://localhost:3000/login', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ email, password }),
    });

    if (!response.ok) {
      throw new Error('Erro ao fazer login');
    }

    return await response.json();
  } catch (error) {
    console.error('Erro na requisição:', error);
    throw error;
  }
};

// API.js
export const processPayment = async (cardNumber, expiryDate, cvv, courseId) => {
  try {
    const response = await fetch('https://example.com/api/payment', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        cardNumber,
        expiryDate,
        cvv,
        courseId,
      }),
    });

    if (!response.ok) {
      throw new Error('Erro ao processar pagamento');
    }

    const data = await response.json();
    return data;  // Retorna a resposta do servidor (por exemplo, confirmação do pagamento)
  } catch (error) {
    throw new Error('Erro ao processar pagamento: ' + error.message);
  }
};

const handleSubmit = async (e) => {
  e.preventDefault();

  // Verificar se os campos estão preenchidos corretamente
  if (!cardNumber || !expiryDate || !cvv) {
    alert('Por favor, preencha todos os campos.');
    return;
  }

  // Validação básica do número do cartão, data de expiração e CVV
  const cardNumberValid = /^[0-9]{16}$/.test(cardNumber);  // Valida se o número do cartão tem 16 dígitos
  const expiryDateValid = /^(0[1-9]|1[0-2])\/\d{2}$/.test(expiryDate);  // Valida o formato MM/AA
  const cvvValid = /^[0-9]{3,4}$/.test(cvv);  // Valida o CVV (3 ou 4 dígitos)

  if (!cardNumberValid || !expiryDateValid || !cvvValid) {
    alert('Por favor, verifique se os dados do cartão estão corretos.');
    return;
  }

  try {
    // Chamar a API para processar a compra
    await processPayment(cardNumber, expiryDate, cvv, selectedCourse.id);
    alert('Compra realizada com sucesso!');
    closeModal(); // Fechar o modal após o envio
  } catch (error) {
    console.error('Erro ao processar o pagamento:', error);
    alert('Erro ao processar pagamento, tente novamente.');
  }
};


// Função para cadastrar um novo usuário
export const registerUser = async (name, email, password) => {
  try {
    const response = await axios.post(`${apiUrl}/cadastro`, { name, email, password });
    return response.data;
  } catch (error) {
    console.error("Erro ao cadastrar usuário:", error);
    throw error;
  }
};

// Função para enviar pedido de redefinição de senha
export const forgotPassword = async (email) => {
  try {
    const response = await axios.post(`${apiUrl}/esqueceu-senha`, { email });
    return response.data;
  } catch (error) {
    console.error("Erro ao solicitar redefinição de senha:", error);
    throw error;
  }
};

// Função para pesquisar cursos
export const searchCourses = async (query) => {
  try {
    const response = await axios.get(`${apiUrl}/search-course`, { params: { query } });
    return response.data;
  } catch (error) {
    console.error("Erro ao pesquisar cursos:", error);
    throw error;
  }
};

// Função para adicionar curso (somente admin)
export const addCourse = async (courseData, userData) => {
  try {
    const response = await axios.post(`${apiUrl}/add-course`, { courseData, userData });
    return response.data;
  } catch (error) {
    console.error("Erro ao adicionar curso:", error);
    throw error;
  }
};

// Função para remover curso (somente admin)
export const removeCourse = async (courseId, userData) => {
  try {
    const response = await axios.delete(`${apiUrl}/remove-course/${courseId}`, { data: { userData } });
    return response.data;
  } catch (error) {
    console.error("Erro ao remover curso:", error);
    throw error;
  }
};

// Função para visualizar detalhes da compra
export const viewPurchaseDetails = async (userId) => {
  try {
    const response = await axios.get(`${apiUrl}/purchase-details/${userId}`);
    return response.data;
  } catch (error) {
    console.error("Erro ao visualizar detalhes da compra:", error);
    throw error;
  }
};
