// src/App.js
import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom';
import Login from './components/Login';
import ForgotPassword from './components/ForgotPassword';
import SearchCourse from './components/SearchCourse';
import AddCourse from './components/AddCourse';
import RemoveCourse from './components/RemoveCourse';
import ViewPurchaseDetails from './components/ViewPurchaseDetails';
import SignUp from './components/SignUp';  // Importando o novo componente

function App() {
  const [userRole, setUserRole] = useState(null); // 'admin' ou 'user'
  const [userData, setUserData] = useState(null); // Armazenar dados do usuário (ex: token)

  return (
    <Router>
      <div>
        {/* Links de navegação */}
        <nav>
          <ul>
            <li>
              <Link to="/">Login</Link>
            </li>
            <li>
              <Link to="/signup">Cadastro</Link>
            </li>
          </ul>
        </nav>

        {/* Controlando a navegação entre telas */}
        <Routes>
          <Route
            path="/"
            element={<Login setUserRole={setUserRole} setUserData={setUserData} />}
          />
          <Route
            path="/signup"
            element={<SignUp setUserRole={setUserRole} setUserData={setUserData} />}
          />
          {userRole === 'admin' && (
            <>
              <Route
                path="/add-course"
                element={<AddCourse userData={userData} />}
              />
              <Route
                path="/remove-course"
                element={<RemoveCourse userData={userData} />}
              />
            </>
          )}
          <Route path="/search-course" element={<SearchCourse />} />
          <Route path="/forgot-password" element={<ForgotPassword />} />
          <Route path="/view-purchase-details" element={<ViewPurchaseDetails />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
