// App.js
import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import Login from './components/Login';
import ForgotPassword from './components/ForgotPassword';
import SearchCourse from './components/SearchCourse';
import AddCourse from './components/AddCourse';
import RemoveCourse from './components/RemoveCourse';
import ViewPurchaseDetails from './components/ViewPurchaseDetails';
import SignUp from './components/SignUp';
import NotAdminModal from './components/NotAdminModal';

function App() {
  const [userRole, setUserRole] = useState(localStorage.getItem('userRole') || null);
  const [userData, setUserData] = useState(JSON.parse(localStorage.getItem('userData')) || null);
  const [isModalOpen, setIsModalOpen] = useState(false);

  useEffect(() => {
    if (userRole) {
      localStorage.setItem('userRole', userRole);
      localStorage.setItem('userData', JSON.stringify(userData));
    }
  }, [userRole, userData]);

  const handleLogout = () => {
    setUserRole(null);
    setUserData(null);
    localStorage.removeItem('userRole');
    localStorage.removeItem('userData');
  };

  const handleAdminRoute = (Component) => {
    if (userRole === 'admin') {
      return <Component userData={userData} />;
    } else {
      setIsModalOpen(true);
      return <Navigate to="/search-course" />;
    }
  };

  return (
    <Router>
      <Routes>
        <Route path="/" element={<Login setUserRole={setUserRole} setUserData={setUserData} />} />
        <Route path="/cadastro" element={<SignUp setUserRole={setUserRole} setUserData={setUserData} />} />
        <Route path="/esqueceu-senha" element={<ForgotPassword />} />

        {userRole ? (
          <>
            <Route path="/search-course" element={<SearchCourse />} />
            <Route path="/view-purchase-details" element={<ViewPurchaseDetails />} />
            <Route path="/add-course" element={handleAdminRoute(AddCourse)} />
            <Route path="/remove-course" element={handleAdminRoute(RemoveCourse)} />
            <Route path="/login" element={<Navigate to="/search-course" />} />
          </>
        ) : (
          <Route path="*" element={<Navigate to="/" />} />
        )}
      </Routes>
      <NotAdminModal isOpen={isModalOpen} onRequestClose={() => setIsModalOpen(false)} />
    </Router>
  );
}

export default App;
