import React, { useState } from 'react';
import { removeCourse } from '../API';

const RemoveCourse = ({ userData }) => {
  const [courseId, setCourseId] = useState('');

  const handleRemoveCourse = async () => {
    try {
      await removeCourse(courseId, userData);
      alert('Curso removido com sucesso!');
    } catch (error) {
      console.error('Erro ao remover curso', error);
    }
  };

  return (
    <div>
      <h1>Remover Curso</h1>
      <input type="text" placeholder="ID do curso" value={courseId} onChange={e => setCourseId(e.target.value)} />
      <button onClick={handleRemoveCourse}>Remover</button>
    </div>
  );
};

export default RemoveCourse;
