import React, { useState } from "react";
import "./PurchaseModal.css"; // Importa o CSS do modal

const PurchaseModal = ({ curso, onClose }) => {
  const [cartao, setCartao] = useState("");

  const handleCompra = () => {
    alert(`Compra do curso "${curso.titulo}" realizada com sucesso!`);
    onClose(); // Fecha o modal
  };

  return (
    <div className="modal-overlay">
      <div className="modal-container">
        <h2>Finalizar Compra</h2>
        <p>Curso: <strong>{curso.titulo}</strong></p>
        
        <input
          type="text"
          placeholder="Número do Cartão"
          value={cartao}
          onChange={(e) => setCartao(e.target.value)}
        />

        <div className="modal-buttons">
          <button className="confirm" onClick={handleCompra}>Confirmar Pagamento</button>
          <button className="cancel" onClick={onClose}>Cancelar</button>
        </div>
      </div>
    </div>
  );
};

export default PurchaseModal;
