import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes, Link, useNavigate } from 'react-router-dom';

// Telas simuladas para fins de teste
const Login = ({ setUserRole, setUserData }) => {
  const navigate = useNavigate();

  const handleLogin = (role) => {
    setUserRole(role);
    setUserData({ token: 'fake_token' });
    if (role === 'admin') {
      navigate('/add-course');  // Redirecionar para a página de adicionar curso
    } else {
      navigate('/search-course');  // Redirecionar para a pesquisa de curso
    }
  };

  return (
    <div>
      <h2>Login</h2>
      <button onClick={() => handleLogin('admin')}>Login como Admin</button>
      <button onClick={() => handleLogin('user')}>Login como Cliente</button>
    </div>
  );
};

const ForgotPassword = () => (
  <div>
    <h2>Esqueceu a Senha</h2>
    <p>Reinicie sua senha aqui.</p>
  </div>
);

const SearchCourse = () => (
  <div>
    <h2>Pesquisar Cursos</h2>
    <p>Aqui você pode pesquisar cursos.</p>
  </div>
);

const AddCourse = () => (
  <div>
    <h2>Adicionar Curso</h2>
    <p>Aqui os administradores podem adicionar novos cursos.</p>
  </div>
);

const RemoveCourse = () => (
  <div>
    <h2>Remover Curso</h2>
    <p>Aqui os administradores podem remover cursos.</p>
  </div>
);

const ViewPurchaseDetails = () => (
  <div>
    <h2>Detalhes da Compra</h2>
    <p>Veja os detalhes das compras feitas.</p>
  </div>
);

function App() {
  const [userRole, setUserRole] = useState(null); // 'admin' ou 'user'
  const [userData, setUserData] = useState(null); // Armazenar dados do usuário (ex: token)

  return (
    <Router>
      <div>
        {/* Rotas e navegação */}
        <Routes>
          {/* Rota de login */}
          <Route path="/" element={<Login setUserRole={setUserRole} setUserData={setUserData} />} />

          {/* Rota de esqueceu senha */}
          <Route path="/forgot-password" element={<ForgotPassword />} />

          {/* Rota de pesquisa de curso */}
          <Route path="/search-course" element={<SearchCourse />} />

          {/* Rota de adicionar curso, só para admin */}
          {userRole === 'admin' && (
            <>
              <Route path="/add-course" element={<AddCourse />} />
              <Route path="/remove-course" element={<RemoveCourse />} />
            </>
          )}

          {/* Detalhes de compra */}
          <Route path="/view-purchase-details" element={<ViewPurchaseDetails />} />
        </Routes>

        {/* Exibição do menu, de acordo com o tipo de usuário */}
        {userRole === 'admin' && (
          <nav>
            <Link to="/add-course">Adicionar Curso</Link> | 
            <Link to="/remove-course">Remover Curso</Link> |
            <Link to="/view-purchase-details">Detalhes da Compra</Link>
          </nav>
        )}
        {userRole === 'user' && (
          <nav>
            <Link to="/search-course">Pesquisar Cursos</Link> |
            <Link to="/view-purchase-details">Detalhes da Compra</Link>
          </nav>
        )}

        {/* Condicional para redirecionar o usuário após login */}
        {userRole === null && (
          <nav>
            <Link to="/">Login</Link>
          </nav>
        )}
      </div>
    </Router>
  );
}

export default App;
