import React, { useState, useEffect } from 'react';
import { searchCourses } from '../API';
import '../style/SearchCourse.css';
import '../style/Course.css';
import '../style/modal.css';

const SearchCourse = () => {
  const [query, setQuery] = useState('');
  const [courses, setCourses] = useState([]);
  const [filteredCourses, setFilteredCourses] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [showModal, setShowModal] = useState(false); 
  const [selectedCourse, setSelectedCourse] = useState(null); 

  // Estado para os dados do cartão
  const [cardNumber, setCardNumber] = useState('');
  const [expiryDate, setExpiryDate] = useState('');
  const [cvv, setCvv] = useState('');

  useEffect(() => {
    const loadCourses = async () => {
      setLoading(true);
      setError(null);
      try {
        const results = await searchCourses('');
        if (results && results.length > 0) {
          setCourses(results);
          setFilteredCourses(results);
        } else {
          setCourses([]);
          setFilteredCourses([]);
        }
      } catch (error) {
        console.error('Erro ao carregar cursos', error);
        setError('Falha ao carregar cursos. Tente novamente mais tarde.');
      } finally {
        setLoading(false);
      }
    };

    loadCourses();
  }, []);

  // Função para filtrar cursos
  const handleSearch = () => {
    if (query) {
      const filtered = courses.filter(course =>
        course.name.toLowerCase().includes(query.toLowerCase())
      );
      setFilteredCourses(filtered);
    } else {
      setFilteredCourses(courses);
    }
  };

  // Função para abrir o modal e selecionar o curso
  const handleBuyClick = (course) => {
    setSelectedCourse(course); 
    setShowModal(true); 
  };

  // Função para fechar o modal
  const closeModal = () => {
    setShowModal(false);
    setSelectedCourse(null);
  };

  // Função para enviar os dados da compra ao backend
  const handleSubmit = async (e) => {
    e.preventDefault();
    
    const purchaseData = {
      courseId: selectedCourse.id,
      courseName: selectedCourse.name,
      courseDescription: selectedCourse.description,
      purchaseDate: new Date().toISOString(),
    };

    try {
      const response = await fetch('/api/purchase', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(purchaseData),
      });

      if (response.ok) {
        // Compra realizada com sucesso
        alert('Compra realizada com sucesso!');
        closeModal(); // Fecha o modal
      } else {
        // Caso ocorra algum erro ao enviar
        alert('Erro ao processar a compra. Tente novamente.');
      }
    } catch (error) {
      console.error('Erro ao enviar os dados de compra', error);
      alert('Erro ao processar a compra. Tente novamente.');
    }
  };

  return (
    <div className="search-container">
      <h1>Pesquisar Cursos</h1>
      <div className="search-box">
        <input
          type="text"
          placeholder="Digite o nome do curso"
          value={query}
          onChange={e => {
            setQuery(e.target.value);
            handleSearch();
          }}
        />
        <button onClick={handleSearch} className="search-btn">
          <i className="fa fa-search"></i>
        </button>
      </div>

      {loading && <p>Carregando...</p>}
      {error && <p className="error-message">{error}</p>}

      <div className="courses-list">
        {filteredCourses.length > 0 ? (
          filteredCourses.map(course => (
            <div key={course.id} className="course-card">
              <img src={course.image} alt={course.name} className="course-image" />
              <div className="course-details">
                <h2>{course.name}</h2>
                <p>{course.description}</p>
                <p className="course-price">{course.price}</p>
                <button className="btn-buy" onClick={() => handleBuyClick(course)}>
                  Comprar
                </button>
              </div>
            </div>
          ))
        ) : (
          <p>Nenhum curso encontrado.</p>
        )}
      </div>

      {/* Modal para inserção de dados do cartão */}
      {showModal && selectedCourse && (
        <div className="modal-overlay">
          <div className="modal">
            <h2>Informações de pagamento - {selectedCourse.name}</h2>
            <form className="payment-form" onSubmit={handleSubmit}>
              <div className="input-group">
                <label htmlFor="card-number">Número do Cartão</label>
                <input
                  type="text"
                  id="card-number"
                  placeholder="**** **** **** ****"
                  value={cardNumber}
                  onChange={(e) => setCardNumber(e.target.value)}
                />
              </div>
              <div className="input-group">
                <label htmlFor="expiry-date">Data de Validade</label>
                <input
                  type="text"
                  id="expiry-date"
                  placeholder="MM/AA"
                  value={expiryDate}
                  onChange={(e) => setExpiryDate(e.target.value)}
                />
              </div>
              <div className="input-group">
                <label htmlFor="cvv">CVV</label>
                <input
                  type="text"
                  id="cvv"
                  placeholder="***"
                  value={cvv}
                  onChange={(e) => setCvv(e.target.value)}
                />
              </div>
              <button type="submit" className="submit-btn">Finalizar Compra</button>
            </form>
            <button className="close-btn" onClick={closeModal}>Fechar</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default SearchCourse;
