// components/NotAdminModal.js
import React from 'react';
import Modal from 'react-modal';

const NotAdminModal = ({ isOpen, onRequestClose }) => {
  return (
    <Modal
      isOpen={isOpen}
      onRequestClose={onRequestClose}
      contentLabel="Acesso Negado"
      ariaHideApp={false}
      style={{
        content: {
          top: '50%',
          left: '50%',
          right: 'auto',
          bottom: 'auto',
          marginRight: '-50%',
          transform: 'translate(-50%, -50%)',
        },
      }}
    >
      <h2>Acesso Negado</h2>
      <div>Você não tem permissão para acessar esta página.</div>
      <button onClick={onRequestClose}>Fechar</button>
    </Modal>
  );
};

export default NotAdminModal;
