import React, { useState } from "react";
import { FaSearch } from "react-icons/fa"; // Importa o ícone de lupa
import CourseCard from "../components/CourseCard";
import "./CoursesList.css"; // Importa o novo CSS da lista

const cursos = [
  { 
    id: 1, 
    titulo: "Curso de React", 
    preco: "R$ 199,00", 
    imagem: "https://www.stickersdevs.com.br/wp-content/uploads/2022/01/java-adesivo-sticker.png" // Imagem pública para o curso de React
  },
  { 
    id: 2, 
    titulo: "Curso de JavaScript", 
    preco: "R$ 150,00", 
    imagem: "https://www.stickersdevs.com.br/wp-content/uploads/2022/01/java-adesivo-sticker.png" // Imagem pública para o curso de JavaScript
  },
  { 
    id: 3, 
    titulo: "Curso de Python", 
    preco: "R$ 120,00", 
    imagem: "https://www.stickersdevs.com.br/wp-content/uploads/2022/01/java-adesivo-sticker.png" // Imagem pública para o curso de Python
  }
];

const CoursesList = () => {
  const [filtro, setFiltro] = useState("");

  const cursosFiltrados = cursos.filter(curso => 
    curso.titulo.toLowerCase().includes(filtro.toLowerCase())
  );

  return (
    <div className="course-page">
      <div className="retangulo-lilas-lupa">
        </div>
      <div className="search-container">
        <FaSearch className="search-icon" />
        <input
          type="text"
          className="search-input"
          placeholder="Pesquisar cursos..."
          value={filtro}
          onChange={(e) => setFiltro(e.target.value)}
        />
        
      </div>
      <div className="retangulo-lilas-lupa">
        </div>
      <div className="course-list">
        {cursosFiltrados.length > 0 ? (
          cursosFiltrados.map(curso => <CourseCard key={curso.id} curso={curso} />)
        ) : (
          <p className="no-courses">Nenhum curso encontrado</p>
        )}
      </div>
    </div>
  );
};

export default CoursesList;
