import React, { useState, useEffect } from 'react';
import { searchCourses } from '../API';
import '../style/SearchCourse.css'; // Estilos para SearchCourse
import '../style/Course.css'; // Estilos para Course

const SearchCourse = () => {
  const [query, setQuery] = useState('');
  const [courses, setCourses] = useState([]);
  const [filteredCourses, setFilteredCourses] = useState([]); // Lista filtrada de cursos
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null); // Estado para armazenar o erro

  // Função para carregar todos os cursos quando o componente for montado
  useEffect(() => {
    const loadCourses = async () => {
      setLoading(true);
      setError(null);  // Reseta o erro antes de tentar buscar os cursos
      try {
        const results = await searchCourses('');  // Passa uma string vazia para pegar todos os cursos
        if (results && results.length > 0) {
          setCourses(results);  // Armazena todos os cursos
          setFilteredCourses(results);  // Inicialmente, a lista filtrada é igual à lista completa
        } else {
          setCourses([]);
          setFilteredCourses([]);  // Garantir que não haja cursos se não forem encontrados
        }
      } catch (error) {
        console.error('Erro ao carregar cursos', error);
        setError('Falha ao carregar cursos. Tente novamente mais tarde.');
      } finally {
        setLoading(false);
      }
    };

    loadCourses(); // Chama a função assim que o componente é montado
  }, []);  // O array vazio [] garante que a chamada seja feita apenas uma vez

  // Função para filtrar cursos com base no query
  const handleSearch = () => {
    if (query) {
      // Filtra cursos com base no nome
      const filtered = courses.filter(course =>
        course.name.toLowerCase().includes(query.toLowerCase())
      );
      setFilteredCourses(filtered);  // Atualiza a lista filtrada
    } else {
      setFilteredCourses(courses);  // Se o campo de pesquisa estiver vazio, mostra todos os cursos
    }
  };

  return (
    <div className="search-container">
      <h1>Pesquisar Cursos</h1>
      <div className="search-box">
        <input
          type="text"
          placeholder="Digite o nome do curso"
          value={query}
          onChange={e => {
            setQuery(e.target.value);  // Atualiza o estado do input
            handleSearch();  // Filtra os cursos enquanto o usuário digita
          }}
        />
        <button onClick={handleSearch} className="search-btn">
          <i className="fa fa-search"></i> {/* Ícone de lupa */}
        </button>
      </div>

      {loading && <p>Carregando...</p>} {/* Exibe mensagem de carregamento */}
      {error && <p className="error-message">{error}</p>} {/* Exibe mensagem de erro */}

      <div className="courses-list">
        {filteredCourses.length > 0 ? (
          filteredCourses.map(course => (
            <div key={course.id} className="course-card">
              <img src={course.image} alt={course.name} className="course-image" />
              <div className="course-details">
                <h2>{course.name}</h2>
                <p>{course.description}</p>
                <p className="course-price">{course.price}</p>
                <button className="btn-buy">Comprar</button>
              </div>
            </div>
          ))
        ) : (
          <p>Nenhum curso encontrado.</p>
        )}
      </div>
    </div>
  );
};

export default SearchCourse;
