import React from 'react';
import { useNavigate } from 'react-router-dom';
import '../style/ViewPurchaseDetails.css'; // Novo arquivo de estilo

const ViewPurchaseDetails = ({ purchases }) => {
  const navigate = useNavigate();

  return (
    <div className="purchase-container">
      <h1>Detalhes das Compras</h1>
      <div className="purchases-list">
        {purchases.length > 0 ? (
          purchases.map((purchase, index) => (
            <div key={index} className="purchase-card">
              <img src={purchase.image} alt={purchase.name} className="purchase-image" />
              <div className="purchase-details">
                <h2>{purchase.name}</h2>
                <p>{purchase.description}</p>
                <p className="purchase-price">Pre√ßo: {purchase.price}</p>
              </div>
            </div>
          ))
        ) : (
          <p>Nenhuma compra realizada.</p>
        )}
      </div>
      <button className="back-btn" onClick={() => navigate(-1)}>Voltar</button>
    </div>
  );
};

export default ViewPurchaseDetails;
