import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom';

// Importar suas telas
import Login from './components/Login';
import ForgotPassword from './components/ForgotPassword';
import SearchCourse from './components/SearchCourse';
import AddCourse from './components/AddCourse';
import RemoveCourse from './components/RemoveCourse';
import ViewPurchaseDetails from './components/ViewPurchaseDetails';

function App() {
  const [userRole, setUserRole] = useState(null); // 'admin' ou 'user'
  const [userData, setUserData] = useState(null); // Armazenar dados do usuário (ex: token)

  return (
    <Router>
      <div>
        {/* Substitua o Switch por Routes */}
        <Routes>
          {/* Rota de login */}
          <Route path="/" element={<Login setUserRole={setUserRole} setUserData={setUserData} />} />

          {/* Rota de esqueceu senha */}
          <Route path="/forgot-password" element={<ForgotPassword />} />

          {/* Rota de pesquisa de curso */}
          <Route path="/search-course" element={<SearchCourse />} />

          {/* Rota de adicionar curso, só para admin */}
          {userRole === 'admin' && (
            <>
              <Route path="/add-course" element={<AddCourse userData={userData} />} />
              <Route path="/remove-course" element={<RemoveCourse userData={userData} />} />
            </>
          )}

          {/* Detalhes de compra */}
          <Route path="/view-purchase-details" element={<ViewPurchaseDetails />} />
        </Routes>

        {/* Exibição do menu, de acordo com o tipo de usuário */}
        {userRole === 'admin' && (
          <nav>
            <Link to="/add-course">Adicionar Curso</Link>
            <Link to="/remove-course">Remover Curso</Link>
          </nav>
        )}
        {userRole === 'user' && (
          <nav>
            <Link to="/search-course">Pesquisar Cursos</Link>
          </nav>
        )}

        {/* Condicional para redirecionar o usuário após login */}
        {userRole && (
          <nav>
            <Link to="/view-purchase-details">Detalhes da Compra</Link>
          </nav>
        )}
      </div>
    </Router>
  );
}

export default App;
