import React, { useState } from 'react';
import { viewPurchaseDetails } from '../API';

const ViewPurchaseDetails = () => {
  const [purchaseDetails, setPurchaseDetails] = useState([]);

  const handleViewDetails = async () => {
    try {
      const details = await viewPurchaseDetails(1); // Altere conforme necessário
      setPurchaseDetails(details);
    } catch (error) {
      console.error('Erro ao obter detalhes da compra', error);
    }
  };

  return (
    <div>
      <h1>Detalhes da Compra</h1>
      <button onClick={handleViewDetails}>Ver detalhes</button>
      <ul>
        {purchaseDetails.map(detail => (
          <li key={detail.id}>{detail.courseName} - {detail.price}</li>
        ))}
      </ul>
    </div>
  );
};

export default ViewPurchaseDetails;
